mapper.select:
select * from (select a.*, rowNum rn from (select * from m_pricing where


		buyer_cd like '%'||#{buyerCd}||'%'



		product_cd like '%'||#{productCd}||'%'



		price between #{startPrice} and #{endPrice}



		startdate <= #{validDate} and enddate >= #{validDate}



		discountrate = #{discountrate}



		currency = #{currency}



del='N' order by adddate desc
)a) where rn between #{startRow} and #{endRow}

select	price
from	m_pricing
where	buyer_cd = #{buyerCd}
	and product_cd = #{productCd}
	and sysdate between startdate and enddate
